  #
  # Does not support locales
  
  sub strftime {			
      local ($template, $sec, $min, $hour, $mday, $mon, $year, $wday, $yday, $isdst) = @_;
  
      undef $@;
      $template =~ s/%([%aAbBcdDefFhHIjklmMnopQrRStTUvwWxXyYZ])/&{$Time::CTime::strftime_conversion{$1}}()/egs;
      die $@ if $@;
      return $template;
  }
  
